settings.custom_liquid:
{% if collection.metafields.custom.faq != blank %}
  <section class="faq-wrap">
    <h2 class="faq-head">Frequently Asked Questions</h2>
    {% for faq in collection.metafields.custom.faq.value %}
      <details class="faq-box">
        <summary class="faq-q">
          {{ faq.question }}
          <span class="faq-icon">+</span>
        </summary>
        <div class="faq-a">{{ faq.answer | metafield_tag }}</div>
      </details>
    {% endfor %}
  </section>
{% endif %}
